INSERT INTO `estate_review` VALUES (3, '2019年世界海洋日暨全国海洋宣传日海报使用说明', '<style type=\"text/css\">#message,#message p,#message span{font-family: &quot;Microsoft YaHei&quot;background-color: rgb(255, 255, 255);}</style><div id=\"doccon\"> \n <div class=\"TRS_Editor\">\n  <div class=\"Custom_UnionStyle\"> \n   <p align=\"justify\">　　为便于各地区各单位结合自身特点开展海洋日宣传活动，今年部统一设计了宣传海报并提供免费下载。本海报仅限于非商业、公益性使用，如用于其他途径，使用者自负法律责任。</p> \n   <p align=\"justify\">　　一、设计规格</p> \n   <p align=\"justify\">　　1. 设计点数：300点。</p> \n   <p align=\"justify\">　　2. 设计尺寸：531mm×766mm，其中四边出血各3mm。</p> \n   <p align=\"justify\">　　3. 文件格式：PNG格式。</p> \n   <p align=\"justify\">　　二、参考图样</p> \n  </div> \n  <p>&nbsp;</p> \n  <p align=\"center\"><img style=\"display:block;margin:auto\" alt=\"\" oldsrc=\"W020190601370594036820.jpg\" src=\"http://127.0.0.1:8081/pic/picture/201906012342418618918858964.jpg\"></p> \n  <p align=\"center\">&nbsp;</p> \n  <p align=\"center\"><img style=\"display:block;margin:auto\" alt=\"\" oldsrc=\"W020190601370594281697.jpg\" src=\"http://127.0.0.1:8081/pic/picture/201906012342422680660582416.jpg\"></p>\n </div> \n</div>', '2019-06-01 00:00:00', 658);

